 export const Background = (para) =>{


    if(para === "haze")
        document.body.style.backgroundImage = "url('./image/hazy_sun.jpeg')"
    else if(para == "broken clouds") 
        document.body.style.backgroundImage = "url('./image/foggy_d_sun.jpeg')"
}
