import { positions } from "@mui/system";
import React, { useEffect, useState } from "react";
import  "./weather.scss";

const WeatherData  = () =>
{
    const [latitude , setLatitude] = useState();
    const [longitude, setLongitude] = useState();
    const llApiUrl = `https://api.ambeedata.com/weather/latest/by-lat-lng?lat=${latitude}&lng=${longitude}`;
    const ApiId = 'cd9389a10700ac6dfe04027b296564c18a1378044aef5adc2140f1c4f4bfc602';
    useEffect(()=>{
        navigator.geolocation.getCurrentPosition((positions)=>{
            setLatitude(positions.coords.latitude);
            setLongitude(positions.coords.longitude);
        })
    },[])

    const getllApi = async ()=>{
        const get = await fetch(`${llApiUrl}&appid=${ApiId}`);
        const res = await get.json();
        console.log(get.res)
    }

    return(
        <>
            <div className="citi_info_wrapper">
                <div className="crnt_city flex-center">
                    <button onClick={()=> getllApi }>
                        <i className="fa-solid fa-location-dot"></i>
                    </button>
                    <h1> Location </h1>
                </div>
                <div className="crnt_city_temp flex-center">
                    <p> crnt Temp </p>
                </div>
            </div>
        </>
    )
}

export default WeatherData;
