import { getValue } from "@testing-library/user-event/dist/utils";
import React, {useState, useEffect} from "react";
import "./weather.scss";
import WeatherData from "./WeatherData";

const Weather =() =>{
    const [latitude ,setLatitude] = useState();
    const [longitude, setLongitude] = useState();
    const [input , setInput] = useState();

    useEffect(()=>{
    navigator.geolocation.getCurrentPosition((position)=>{
        setLatitude(position.coords.latitude);
        setLongitude(position.coords.longitude);
    })
    },[])
    const getValue = (event) =>{
        setInput(event.target.value);
    }
    return(
        <>
            <div className="header_wrapper">
                <div className="search_wrapper flex-center">
                    <div className="search_bar_wrapper flex-center">
                        <div className="search_bar flex-center">
                            <input 
                                type="text"
                                placeholder="Enter City Name"
                                onChange={getValue}
                                value={input}
                            />
                            <button className="client_location_btn"> <i className="fa-solid fa-location-crosshairs"></i> </button>
                        </div>
                        <div className="suggestion_section">
                        </div>
                    </div>
                        <button className="search_btn"> Search</button>
                </div>
            </div>
        </>
    )
}
export default Weather;
