import React, { useEffect, useState } from "react";
import  "./weather.scss";

const WeatherData  = (props) =>
{
    const [searchedCity, setSearchedCity] = useState("");
    const [crntCityLat , setCrntCityLat] = useState();
    const [crntCityLon , setCrntCityLon] = useState();
    const [temprature, setTemprature] = useState([]);

    useEffect(()=>{
        const callApi = async ()=>{
            const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${props.city}&units=metric&appid=9e4cdd6c628c4cbdcacc25b6df594009`);
            const result = await response.json();
            setSearchedCity(result.name);
            setTemprature(result.main);
            setCrntCityLat(result.coord.lon);
            setCrntCityLon(result.coord.lon)
        }
        callApi();
        const callForcastApi = async ()=>{
            const response = await fetch(`https://pro.openweathermap.org/data/2.5/forecast/hourly?lat=${crnCitytLat}&lon=${crnCitytLon}&appid=9e4cdd6c628c4cbdcacc25b6df594009`);
            const result = await response.json();
            console.log(result);
        }
        callForcastApi();
    },[props]);

    return(
        <>
            <div className="citi_info_wrapper">
                <div className="crnt_city flex-center">
                        <i className="fa-solid fa-location-dot"></i>
                    <h1> {props.city} </h1>
                </div>
                <div className="crnt_city_temp flex-center">
                    <p> {temprature.temp} </p>
                </div>
            </div>
        </>
    )
}

export default WeatherData;
