 export const Background = (para) =>{
    if(para === "haze")
        document.body.style.background = "url('./image/hazy_sun.jpeg') center/auto no-repeat"
    else if(para == "broken clouds") 
        document.body.style.background = "url('./image/foggy_d.jpeg') center/cover no-repeat "
    else document.body.style.background = 'url("./image/cloudy_animated_d.jpeg") no-repeat'
}
